// A map to hold our Audio objects
const audioMap: { [key: string]: HTMLAudioElement } = {};
let _isMuted = false;
let isInitialized = false;

// Base64 encoded sound data to prevent needing external asset files
const soundData = {
  music: 'data:audio/wav;base64,-/',
  move: 'data:audio/wav;base64,UklGRjIAAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YUgAAAAiIiRGR0lKTlBSU1teX2BhYmZlaGlqa25zdHV2d3h6fH1/gIGCg4WGh4iJiouMjY6PkJGSk5SVlpeYmZqbnJ2en6ChoqOkpaanqKmqq6ytrq+wsbKztLW2t7i5uru8vb6/wMHCw8TFxsfIycjLyw==',
  correct: 'data:audio/wav;base64,UklGRkoAAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YUYAAAB/f3+AgYKDhIWGh4iJiouMjY6PkJGSk5SVlpeYmZqbnJ2en6ChoqOkpaanqKmqq6ytrq+wsbKztLW2t7i5uru8vb6/wMHCw8TFxsfIycjLyw==',
  incorrect: 'data:audio/wav;base64,UklGRkgAAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YEEAAAAA////3d3dzc3NxcXFvb29sbGxnZ2djY2NfX19dXV1cnJybm5ubGxsa2tpaWloaGdnZ2ZgYGBgYWBfX19eXl1dXVxcXFtbW1paWllZWVhYWFdXV1ZWVVVVVFNTUlJSUFBQT09PTk5OTU1NTExMS0pKSkhISEdHR0ZGRkVERUNDQ0JCQUFAQD8/Pz4+PT08PDs7Ozk5OTg4ODc3NzY2NjU1NjU1',
  gameOver: 'data:audio/wav;base64,-/',
};

/**
 * Initializes the audio elements. This should be called once after a user interaction.
 */
export const initializeAudio = () => {
  if (isInitialized) return;

  Object.keys(soundData).forEach(key => {
    const audio = new Audio(soundData[key as keyof typeof soundData]);
    audio.preload = 'auto';
    audioMap[key] = audio;
  });

  if (audioMap.music) {
    audioMap.music.loop = true;
  }
  isInitialized = true;
};

const play = (audio: HTMLAudioElement) => {
    // Play audio, catching and logging potential browser errors
    audio.play().catch(e => console.error("Error playing audio:", e));
}

/**
 * Plays a sound effect by its name.
 * @param name The name of the sound effect to play.
 */
export const playSound = (name: keyof typeof soundData) => {
  if (!isInitialized || _isMuted) return;
  
  const audio = audioMap[name];
  if (audio) {
    audio.currentTime = 0; // Rewind to start
    play(audio);
  }
};

/**
 * Starts playing the background music.
 */
export const playMusic = () => {
  if (!isInitialized || _isMuted) return;
  if (audioMap.music && audioMap.music.paused) {
    play(audioMap.music);
  }
};

/**
 * Stops the background music.
 */
export const stopMusic = () => {
  if (!isInitialized) return;
  if (audioMap.music) {
    audioMap.music.pause();
    audioMap.music.currentTime = 0;
  }
};

/**
 * Toggles the mute state for all sounds.
 * @returns The new mute state (true if muted, false otherwise).
 */
export const toggleMute = (): boolean => {
    _isMuted = !_isMuted;
    if (_isMuted) {
        // Stop all sounds
        Object.values(audioMap).forEach(audio => {
            if (audio) {
                audio.pause();
                audio.currentTime = 0;
            }
        });
    } else {
        // If we are unmuting, music will be restarted by the game logic in App.tsx
        // if the game state is currently 'PLAYING'.
    }
    return _isMuted;
};

/**
 * Gets the current mute state.
 * @returns True if muted, false otherwise.
 */
export const isMuted = (): boolean => {
    return _isMuted;
}
